synthesis_workflow.step:
CYPHER 25 MATCH (orch:NavigationMaster:Orchestrator {namespace: $orchestration_ns}) CREATE (master:NavigationMaster:MasterSynthesis { namespace: $orchestration_ns + '_master', created_at: datetime(), topology: 'HYBRID', total_agents: $agent_count, total_issues: $total_issues, total_components: $total_components, synthesis_confidence: $confidence, ai_description: 'Unified knowledge graph synthesizing ' + $agent_count + ' parallel analyses',
// AUTO-DISCOVERY for master synthesis query_catalog_json: '{"get_all_issues":"MATCH (master)-[*1..3]->(i:UnifiedIssue) RETURN i","get_categories":"MATCH (master)-[:HAS_CATEGORY]->(cat) RETURN cat"}', schema_instructions_json: '{"entry":"MasterSynthesis","structure":"Categories ‚Üí UnifiedIssues","insights":"Aggregated from ' + $agent_count + ' agents"}' }) MERGE (master)-[:SYNTHESIZES]->(orch)
// Create insight categories WITH master UNWIND ['Critical Issues', 'Architectural Patterns', 'Performance Bottlenecks', 'Security Vulnerabilities', 'Code Quality', 'Refactoring Opportunities'] as category CREATE (cat:InsightCategory { name: category, created_at: datetime() }) MERGE (master)-[:HAS_CATEGORY]->(cat)
RETURN master, [(master)-[:HAS_CATEGORY]->(c) | c.name] as categories
