HYPERSPEED_DISCOVERY.powershell_discovery:
# Alexandria Hyperspeed Discovery
param([string]$BasePath, [string]$Pattern = "*.*", [string]$Namespace, [int]$BatchSize = 100)

Write-Host "ðŸ§  Alexandria awakens..." -ForegroundColor Cyan
if (-not (Test-Path $BasePath)) { Write-Error "Path not found: $BasePath"; return }

$start = [System.Diagnostics.Stopwatch]::StartNew()

# Entity recognizers (compiled for speed)
$recognizers = @{
    STATE_MACHINE = [regex]::new('Controller|Handler|Endpoint|Resource|Api|Rest', 'Compiled')
    CONFIG_STATE = [regex]::new('Config|Settings|Properties|Environment', 'Compiled')
    SECURITY_LAYER = [regex]::new('Security|Auth|Guard|Permission|Token', 'Compiled')
    FILE_REGISTRY = [regex]::new('Service|Repository|Impl|DAO|Provider', 'Compiled')
    DIAGNOSTIC_ENGINE = [regex]::new('Test|Log|Exception|Error|Monitor', 'Compiled')
    TEMPORAL_CONTEXT = [regex]::new('Schedule|Timer|Cron|Async|Cache', 'Compiled')
}

# High-speed file discovery
$searcher = [System.IO.DirectoryInfo]::new($BasePath)
$files = $searcher.GetFiles($Pattern, [System.IO.SearchOption]::AllDirectories)

$start.Stop()
Write-Host "âš¡ Discovered $($files.Count) files in $($start.ElapsedMilliseconds)ms" -ForegroundColor Green

# Parallel entity classification
$classified = $files | ForEach-Object -Parallel {
    $file = $_
    $recs = $using:recognizers
    $base = $using:BasePath

    $relative = $file.FullName.Replace($base, '').TrimStart('\')
    $entity = 'UNKNOWN'
    $confidence = 0.0

    # Quick classification
    foreach ($type in $recs.Keys) {
        if ($recs[$type].IsMatch($file.Name)) {
            $entity = $type
            $confidence = 0.9
            break
        }
    }

    # Path-based inference if unknown
    if ($entity -eq 'UNKNOWN') {
        $path = $relative.ToLower()
        if ($path -match 'controller') { $entity = 'STATE_MACHINE'; $confidence = 0.85 }
        elseif ($path -match 'config') { $entity = 'CONFIG_STATE'; $confidence = 0.85 }
        elseif ($path -match 'security|auth') { $entity = 'SECURITY_LAYER'; $confidence = 0.85 }
        elseif ($path -match 'service|repository') { $entity = 'FILE_REGISTRY'; $confidence = 0.85 }
        elseif ($path -match 'test') { $entity = 'DIAGNOSTIC_ENGINE'; $confidence = 0.85 }
    }

    # Java annotation detection for high confidence
    if ($file.Extension -eq '.java' -and $file.Length -lt 50KB) {
        try {
            $content = [System.IO.File]::ReadAllText($file.FullName)
            if ($content -match '@RestController|@Controller') { $entity = 'STATE_MACHINE'; $confidence = 0.95 }
            elseif ($content -match '@Service|@Component') { $entity = 'FILE_REGISTRY'; $confidence = 0.95 }
            elseif ($content -match '@Configuration') { $entity = 'CONFIG_STATE'; $confidence = 0.95 }
            elseif ($content -match '@Test') { $entity = 'DIAGNOSTIC_ENGINE'; $confidence = 0.95 }
        } catch {}
    }

    [PSCustomObject]@{
        Id = "FILE_$($file.Name.GetHashCode())_$($file.Length)"
        Name = $file.BaseName
        FullName = $file.Name
        FullPath = $file.FullName
        RelativePath = $relative
        Entity = $entity
        Confidence = $confidence
        Size = $file.Length
        Extension = $file.Extension
        Modified = $file.LastWriteTime.ToString('yyyy-MM-dd HH:mm:ss')
    }
} -ThrottleLimit 10

# Statistics
$stats = $classified | Group-Object Entity | ForEach-Object {
    "$($_.Name): $($_.Count) files"
} | Join-String -Separator ", "

Write-Host "ðŸ“Š Distribution: $stats" -ForegroundColor Yellow

# Output for Neo4j
@{
    Metadata = @{Timestamp = Get-Date -Format 'yyyy-MM-dd HH:mm:ss'; Namespace = $Namespace}
    Files = $classified
} | ConvertTo-Json -Depth 10 -Compress

HYPERSPEED_DISCOVERY.cypher_integration:
CYPHER 25 // Alexandria Neo4j Integration with Embeddings
// Stage 1: NavigationMaster (ErdÅ‘s 0) MERGE (nav:NavigationMaster {id: 'NAV_' + $namespace, namespace: $namespace}) ON CREATE SET nav.created_at = datetime(), nav.erdos_number = 0, nav.hierarchy_level = 1 SET nav.files_in_awareness = coalesce(nav.files_in_awareness, 0) + size($batch)
// Stage 2: Process files WITH nav UNWIND $batch as file WITH nav, file, CASE WHEN file.Package STARTS WITH 'com.' THEN split(file.Package, '.')[2] WHEN file.Package CONTAINS '.' THEN split(file.Package, '.')[0] ELSE 'core' END as domain_name
// Stage 3: Domain subgraph MERGE (domain:DomainSubgraph {id: 'DOM_' + $namespace + '_' + domain_name, namespace: $namespace}) ON CREATE SET domain.hierarchy_level = 2, domain.erdos_number = 1 MERGE (nav)-[:HAS_DOMAIN]->(domain)
// Stage 4: File nodes WITH nav, domain, file MERGE (f:File {id: 'FILE_' + $namespace + '_' + file.Id, full_path: file.FullPath}) ON CREATE SET f.hierarchy_level = 3, f.erdos_number = 3 SET f.name = file.Name, f.entity_type = file.Entity, f.confidence = file.Confidence, f.size_bytes = file.Size, f.modified = datetime(file.Modified), f.awaiting_embedding = true
MERGE (domain)-[:CONTAINS]->(f)
// Stage 5: Generate embeddings WITH f WHERE f.awaiting_embedding = true WITH f, f.name + ' ' + f.entity_type + ' ' + CASE f.entity_type WHEN 'STATE_MACHINE' THEN 'controller endpoint api rest' WHEN 'CONFIG_STATE' THEN 'configuration settings properties' WHEN 'SECURITY_LAYER' THEN 'security authentication authorization' WHEN 'FILE_REGISTRY' THEN 'service repository business logic' WHEN 'DIAGNOSTIC_ENGINE' THEN 'test error exception debug' WHEN 'TEMPORAL_CONTEXT' THEN 'schedule timer async cron' ELSE '' END as text_to_embed
MATCH (config:APIConfig {name: 'openai'}) CALL genai.vector.encode( substring(text_to_embed, 0, 8000), "OpenAI", {token: config.token, model: 'text-embedding-3-large', dimensions: 3072} ) YIELD embedding
SET f.embedding = embedding, f.has_embedding = true, f.awaiting_embedding = false
// Stage 6: Create vector index WITH nav CALL apoc.do.when( NOT apoc.schema.node.indexExists('File', ['embedding']), 'CALL db.index.vector.createNodeIndex("file-embeddings", "File", "embedding", 3072, "cosine") YIELD name RETURN name', 'RETURN "exists" as name', {} ) YIELD value
// Stage 7: Find similar files WITH nav MATCH (f:File {namespace: nav.namespace}) WHERE f.has_embedding = true CALL db.index.vector.queryNodes('file-embeddings', 5, f.embedding) YIELD node as similar, score WHERE id(f) <> id(similar) AND score > 0.85 CREATE (f)-[:SIMILAR_TO {similarity: score}]->(similar)
RETURN {namespace: nav.namespace, files_indexed: nav.files_in_awareness} as result

REALTIME_MONITORING.filesystem_watcher:
# Alexandria Real-time Monitor
param([string]$WatchPath, [string]$Namespace)

$watcher = New-Object System.IO.FileSystemWatcher
$watcher.Path = $WatchPath
$watcher.IncludeSubdirectories = $true
$watcher.EnableRaisingEvents = $true

$processEvent = {
    param($Type, $Path)
    $entity = 'UNKNOWN'
    $file = Split-Path $Path -Leaf

    # Quick classification
    if ($file -match 'Controller') { $entity = 'STATE_MACHINE' }
    elseif ($file -match 'Service') { $entity = 'FILE_REGISTRY' }
    elseif ($file -match 'Config') { $entity = 'CONFIG_STATE' }
    elseif ($file -match 'Test') { $entity = 'DIAGNOSTIC_ENGINE' }

    Write-Host "[$Type] $file [$entity]" -ForegroundColor Yellow

    # Queue for Neo4j update
    @{Type=$Type; Path=$Path; Entity=$entity; Time=(Get-Date)} | ConvertTo-Json -Compress
}

Register-ObjectEvent $watcher "Created" -Action { & $processEvent 'Created' $Event.SourceEventArgs.FullPath }
Register-ObjectEvent $watcher "Changed" -Action { & $processEvent 'Changed' $Event.SourceEventArgs.FullPath }
Register-ObjectEvent $watcher "Deleted" -Action { & $processEvent 'Deleted' $Event.SourceEventArgs.FullPath }

Write-Host "ðŸ‘ï¸ Monitoring $WatchPath" -ForegroundColor Cyan
while ($true) { Start-Sleep -Seconds 1 }
